import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/global.css';
import '../styles/home.css';

const About = ({ theme, toggleTheme }) => {
  const [submitMessage, setSubmitMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [aboutData, setAboutData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    setSubmitMessage('');
    setMessageType('');

    const formData = new FormData(e.target);
    const data = Object.fromEntries(formData);

    try {
      const response = await fetch('http://localhost:3001/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (response.ok) {
        setSubmitMessage('Спасибо! Ваше сообщение отправлено.');
        setMessageType('success');
        e.target.reset();
      } else {
        setSubmitMessage(result.error || 'Ошибка отправки');
        setMessageType('error');
      }
    } catch (error) {
      setSubmitMessage('Ошибка подключения к серверу');
      setMessageType('error');
      console.error('Fetch error:', error);
    }
  };

  useEffect(() => {
    const fetchAboutData = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/about');
        if (!response.ok) {
          throw new Error('Ошибка загрузки данных');
        }
        const data = await response.json();
        setAboutData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAboutData();
  }, []);

  const logoPath = theme === 'dark' ? process.env.PUBLIC_URL + '/logo_dark.svg' : process.env.PUBLIC_URL + '/logo_Theme.svg';

  if (loading) {
    return (
      <div className="landing-page">
        <div className="container">
          <p>Загрузка...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="landing-page">
        <div className="container">
          <p>Ошибка: {error}</p>
        </div>
      </div>
    );
  }

  if (!aboutData) {
    return null;
  }

  return (
    <div className="landing-page">
      <header className="landing-header">
        <div className="container">
          <nav className="landing-nav">
            <Link to="/" className="landing-logo">
              <img className='logo home_logo' src={logoPath} alt="AxionLabs Logo" />
            </Link>
            <div className="landing-auth">
              <Link to="/products" className="btn btn-primary">Продукты</Link>
              <button className="theme-toggle" onClick={toggleTheme}>
                <i className={theme === 'dark' ? 'fas fa-moon' : 'fas fa-sun'}></i>
              </button>
            </div>
          </nav>
        </div>
      </header>

      <section className="hero-section">
        <div className="container">
          <div className="hero-content">
            <div className="hero-text">
              <div className="hero-header-row">
                <div className="hero-header-text">
                  <h1 className="hero-title">{aboutData.hero.title}</h1>
                  <p className="hero-subtitle">{aboutData.hero.subtitle}</p>
                </div>

                <div className="hero-image">
                  <div className="hero-image-placeholder">
                    AxionLabs
                  </div>
                </div>
              </div>

              <div className="hero-features">
                {aboutData.hero.features.map((feature, index) => (
                  <div key={index} className="feature-item">
                    <div className="feature-icon">
                      <i className={feature.icon}></i>
                    </div>
                    <span>{feature.text}</span>
                  </div>
                ))}
              </div>

              <a href="#contact" className="btn btn-primary">
                <i className="fas fa-envelope"></i> Связаться с нами
              </a>
            </div>
          </div>
        </div>
      </section>

      <section className="about-section">
        <div className="container">
          <h2 className="section-title">{aboutData.about.title}</h2>
          <p className="about-description">{aboutData.about.description}</p>
          <p className="about-description">{aboutData.about.mission}</p>
          <ul className="about-values">
            {aboutData.about.values.map((value, index) => (
              <li key={index}>{value}</li>
            ))}
          </ul>
        </div>
      </section>

      <section className="features-section">
        <div className="container">
          <h2 className="section-title">Наши услуги</h2>

          <div className="features-grid">
            {aboutData.services.map((service, index) => (
              <div key={index} className="feature-card">
                <div className="feature-card-icon">
                  <i className={service.icon}></i>
                </div>
                <h3 className="feature-card-title">{service.title}</h3>
                <p className="feature-card-desc">{service.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section id="contact" className="contact-section">
        <div className="container">
          <h2 className="section-title">{aboutData.contact.title}</h2>
          <p className="contact-description">{aboutData.contact.description}</p>

          <div className="contact-form">
            <h3 className="form-title">Форма обратной связи</h3>
            <form onSubmit={handleFeedbackSubmit}>
              <div className="form-group">
                <label htmlFor="feedbackName">Имя</label>
                <input type="text" id="feedbackName" name="from_name" required placeholder="Введите ваше имя" />
              </div>

              <div className="form-group">
                <label htmlFor="feedbackEmail">Email</label>
                <input type="email" id="feedbackEmail" name="from_email" required placeholder="Введите ваш email" />
              </div>

              <div className="form-group">
                <label htmlFor="feedbackMessage">Сообщение</label>
                <textarea id="feedbackMessage" name="message" required placeholder="Расскажите о вашем проекте или задайте вопрос"></textarea>
              </div>

              <button type="submit" className="btn btn-primary">
                Отправить сообщение
              </button>
            </form>
            {submitMessage && <p className={`submit-message ${messageType}`}>{submitMessage}</p>}
          </div>
        </div>
      </section>

      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3 className="footer-title">AxionLabs</h3>
              <p>Профессиональная разработка программного обеспечения</p>
              <img src={logoPath} alt="AxionLabs Logo" className="footer-logo" />
            </div>

            <div className="footer-section">
              <h3 className="footer-title">Контакты</h3>
              <ul className="footer-links">
                <li><i className="fas fa-envelope"></i><a href={`mailto:${aboutData.contact.email}`}>{aboutData.contact.email}</a></li>
                <li><i className="fas fa-phone"></i> <a href={`tel:${aboutData.contact.phone}`}>{aboutData.contact.phone}</a></li>
                <li><i className="fas fa-map-marker-alt"></i> {aboutData.contact.address}</li>
              </ul>
            </div>

            <div className="footer-section">
              <h3 className="footer-title">Быстрые ссылки</h3>
              <ul className="footer-links">
                <li><Link to="/">Главная</Link></li>
                <li><Link to="/products">Продукты</Link></li>
              </ul>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; 2025 AxionLabs. Все права защищены.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default About;
